mapper.update:
update emp set username=#{username}, name=#{name}, gender=#{gender},
               image=#{image}, job=#{job}, entrydate=#{entrydate},
               dept_id=#{deptId}, update_time=#{updateTime}
where id = #{id}

mapper.select:
select id, username, password, name, gender, image, job, entrydate,
       dept_id, create_time, update_time from emp

    name like concat('%', #{name}, '%')
    and gender = #{gender}
    and entrydate between #{begin} and #{end}

order by update_time desc
